mapper.select:
select * from(
select * from product join member using(company_no))
join (select * from subcategory join maincategory using(main_category_no))
using(sub_category_no)
where main_category_no=#{mainCategoryNo}
and sub_category_no=#{subCategoryNo}

mapper.update:
update product set
product_name=#{productName},
product_price#{price},
product_count=#{productCount}
where product_no=#{productNo}

mapper.select:
select * from(
select * from board join product
using(product_no)) join subcategory
using(sub_category_no)
where sub_category_no=#{subCategoryNo}
order by board_no

mapper.update:
update board
set
board_title=#{boardTitle},
board_content=#{boardContent}
where board_no = #{boardNo}

mapper.select:
select * from review join
member
using(member_no)
where board_no=#{boardNo}
order by 1

select * from review
join member
using(member_no)
where review_no=#{reviewNo}

mapper.update:
update review
set
review_content=#{reviewContent},
score=#{score},
reviewAttach=#{reviewAttach}
where review_no=#{reviewNo}
